import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { motion, useAnimation, useScroll, useTransform } from 'framer-motion'
import { AcademicCapIcon, HandRaisedIcon, HeartIcon, ComputerDesktopIcon, UserGroupIcon, UserIcon, BookOpenIcon, GlobeAltIcon, SparklesIcon } from '@heroicons/react/24/outline'

const Home = () => {
  const controls = useAnimation()
  const { scrollYProgress } = useScroll()
  const scale = useTransform(scrollYProgress, [0, 1], [1, 1.2])
  const opacity = useTransform(scrollYProgress, [0, 0.3], [1, 0])
  const backgroundY = useTransform(scrollYProgress, [0, 1], ['0%', '50%'])

  const animations = {
    fadeIn: { initial: { opacity: 0, y: 20 }, animate: { opacity: 1, y: 0 }, transition: { duration: 0.6 }},
    hover: { scale: 1.05, rotate: 2, transition: { type: "spring", stiffness: 300 }},
    container: { hidden: { opacity: 0 }, visible: { opacity: 1, transition: { delayChildren: 0.3, staggerChildren: 0.2 }}},
    item: { hidden: { y: 20, opacity: 0 }, visible: { y: 0, opacity: 1, transition: { type: "spring", stiffness: 100 }}}
  }

  useEffect(() => {
    controls.start({ scale: [1, 1.2, 1], rotate: [0, 5, -5, 0] })
      .then(() => controls.start({ y: [0, -20, 0], transition: { repeat: Infinity, duration: 2 }}))
  }, [controls])

  const renderSection = (title, items, bgClass = "") => (
    <motion.div className={`py-16 ${bgClass}`} initial={{ opacity: 0 }} whileInView={{ opacity: 1 }} viewport={{ once: false }}>
      <motion.h2 className="text-4xl font-bold text-white text-center mb-12" animate={{ color: ["#fff", "#60A5FA", "#fff"] }} transition={{ duration: 3, repeat: Infinity }}>
        {title}
      </motion.h2>
      <motion.div className="grid grid-cols-1 md:grid-cols-3 gap-8" variants={animations.container} initial="hidden" whileInView="visible">
        {items.map((item, index) => (
          <motion.div key={index} className="p-8 bg-white/5 backdrop-blur-lg rounded-xl shadow-2xl border border-white/10" variants={animations.item} whileHover={animations.hover}>
            {item.icon}
            <h3 className="text-xl font-bold text-white mb-4">{item.title}</h3>
            <p className="text-gray-300">{item.desc}</p>
          </motion.div>
        ))}
      </motion.div>
    </motion.div>
  )

  return (
    <motion.div className="min-h-screen bg-[#0e1e4b] relative overflow-hidden" style={{ backgroundPositionY: backgroundY }}>
      <motion.div className="absolute inset-0 bg-gradient-to-b from-blue-500/20 to-purple-500/20" style={{ opacity: scrollYProgress }} />
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div initial={{ opacity: 0, y: -50 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.8 }} className="pt-20 pb-12" style={{ scale, opacity }}>
          <div className="flex items-center justify-center space-x-12">
            <motion.img src={require("../assets/genlink-logo.png")} alt="GenLink Logo" className="h-40" animate={controls} initial={{ scale: 0.5 }} />
            <div className="text-left">
              <motion.h1 className="text-6xl font-bold text-white mb-6" animate={{ textShadow: ["0 0 5px #fff", "0 0 20px #fff", "0 0 5px #fff"] }} transition={{ duration: 2, repeat: Infinity }}>
                Welcome to GenLink
              </motion.h1>
              <motion.p className="text-2xl text-gray-300 max-w-3xl" {...animations.fadeIn}>
                Bridging generations through technology, creating meaningful connections that last a lifetime.
              </motion.p>
            </div>
          </div>
        </motion.div>

        {renderSection("Our Core Services", [
          { icon: <ComputerDesktopIcon className="w-10 h-10 text-blue-400 mb-4" />, title: "Interactive Learning", desc: "Engage in creative projects that bridge generations through technology and shared experiences." },
          { icon: <UserGroupIcon className="w-10 h-10 text-purple-400 mb-4" />, title: "Community Building", desc: "Foster meaningful connections in our vibrant community through mentorship and events." },
          { icon: <AcademicCapIcon className="w-10 h-10 text-green-400 mb-4" />, title: "Personal Growth", desc: "Develop new skills and expand your horizons through various learning opportunities." }
        ])}

        {renderSection("Global Impact", [
          { icon: <GlobeAltIcon className="w-10 h-10 text-yellow-400 mb-4" />, title: "Worldwide Reach", desc: "Connect with people from different cultures and backgrounds across the globe." },
          { icon: <BookOpenIcon className="w-10 h-10 text-red-400 mb-4" />, title: "Knowledge Exchange", desc: "Share experiences and learn from diverse perspectives and expertise." },
          { icon: <SparklesIcon className="w-10 h-10 text-indigo-400 mb-4" />, title: "Innovation Hub", desc: "Stay at the forefront of technological advancement and social innovation." }
        ], "bg-white/5")}

        <motion.div className="py-16 bg-white/5 backdrop-blur-lg rounded-xl shadow-2xl border border-white/10 px-8 mb-16" initial={{ opacity: 0, y: 50 }} whileInView={{ opacity: 1, y: 0 }}>
          <div className="text-center">
            <motion.h2 className="text-4xl font-bold text-white mb-8" animate={{ textShadow: ["0 0 5px #fff", "0 0 20px #60A5FA", "0 0 5px #fff"] }} transition={{ duration: 2, repeat: Infinity }}>
              Join Our Community
            </motion.h2>
            <div className="flex justify-center gap-4">
              <Link to="/register" className="px-8 py-4 bg-blue-500 text-white rounded-lg font-medium hover:bg-blue-600 transition-all duration-200">Get Started</Link>
              <Link to="/login" className="px-8 py-4 bg-white/10 text-white rounded-lg font-medium hover:bg-white/20 transition-all duration-200">Sign In</Link>
            </div>
          </div>
        </motion.div>
      </div>
    </motion.div>
  )
}

export default Home
