import React, { useState, useEffect } from 'react'
import { auth, db } from '../firebase/firebase-config'
import { updatePassword, reauthenticateWithCredential, EmailAuthProvider } from 'firebase/auth'
import { useNavigate } from 'react-router-dom'
import { doc, getDoc, updateDoc, collection, getDocs } from 'firebase/firestore'

const Dashboard = () => {
  const navigate = useNavigate()
  const [state, setState] = useState({
    firstName: '', lastName: '', email: '', activeComponent: 'dashboard', volunteerHours: 0, 
    isEditing: false, password: '', currentPassword: '', bio: '', originalData: null, volunteerLog: [], members: []
  })

  const buttonBaseClasses = "w-full py-3 px-6 text-white rounded-lg font-medium transition-all duration-300 shadow-lg transform hover:scale-[1.02]"

  useEffect(() => {
    const fetchUserData = async () => {
      if (!auth.currentUser) return console.error('No authenticated user found');
      const userSnap = await getDoc(doc(db, 'users', auth.currentUser.uid))
      if (userSnap.exists()) {
        const data = userSnap.data()
        setState(prev => ({
          ...prev, 
          ...data,
          email: auth.currentUser.email,
          originalData: { firstName: data.firstName, lastName: data.lastName, email: auth.currentUser.email, bio: data.bio }
        }))
      } else {
        console.error('User document does not exist')
      }
    }
    fetchUserData()
  }, [])

  const handleUpdateProfile = async () => {
    try {
      await updateDoc(doc(db, 'users', auth.currentUser.uid), { firstName: state.firstName, lastName: state.lastName, bio: state.bio, isPublic: true });
      if (state.password.trim()) {
        if (!state.currentPassword) throw new Error('Enter current password to update your password.')
        await reauthenticateWithCredential(auth.currentUser, EmailAuthProvider.credential(auth.currentUser.email, state.currentPassword));
        await updatePassword(auth.currentUser, state.password)
      }
      setState(prev => ({ ...prev, isEditing: false, password: '', currentPassword: '' }))
    } catch (error) {
      console.error('Error updating profile:', error)
      alert('Failed to update profile: ' + error.message)
    }
  }

  const handleCancel = () => {
    setState(prev => ({ ...prev, ...prev.originalData, password: '', currentPassword: '', isEditing: false }))
  }

  const components = {
    profile: { title: 'Your Profile', content: ProfileComponent() },
    volunteer: { title: 'Volunteer Activity', content: VolunteerComponent() },
    members: { title: 'Members', content: MembersComponent() }, 
    // Note: Events component omitted for brevity, similar to the other components
  }

  const ProfileComponent = () => (
    <div className="bg-white/10 p-6 rounded-xl">
      {state.isEditing ? (
        <>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <input type="text" value={state.firstName} onChange={e => setState(prev => ({ ...prev, firstName: e.target.value }))} className="w-full p-3 rounded-lg bg-white/10 text-white" placeholder="First Name" />
            <input type="text" value={state.lastName} onChange={e => setState(prev => ({ ...prev, lastName: e.target.value }))} className="w-full p-3 rounded-lg bg-white/10 text-white" placeholder="Last Name" />
          </div>
          <input type="email" value={state.email} disabled className="w-full p-3 rounded-lg bg-white/10 text-white opacity-50" />
          <textarea value={state.bio} onChange={e => setState(prev => ({ ...prev, bio: e.target.value }))} className="w-full p-3 rounded-lg bg-white/10 text-white" placeholder="Bio" rows="4"/>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <input type="password" value={state.currentPassword} onChange={e => setState(prev => ({ ...prev, currentPassword: e.target.value }))} className="w-full p-3 rounded-lg bg-white/10 text-white" placeholder="Current Password" />
            <input type="password" value={state.password} onChange={e => setState(prev => ({ ...prev, password: e.target.value }))} className="w-full p-3 rounded-lg bg-white/10 text-white" placeholder="New Password" />
          </div>
          <div className="flex justify-between">
            <button onClick={handleUpdateProfile} className={`${buttonBaseClasses} bg-green-500`}>Save Changes</button>
            <button onClick={handleCancel} className={`${buttonBaseClasses} bg-gray-500`}>Cancel</button>
          </div>
        </>
      ) : (
        <div>
          <h3>{state.firstName} {state.lastName}</h3>
          <p>{state.email}</p>
          <p>{state.bio || 'No bio added yet.'}</p>
          <button onClick={() => setState(prev => ({ ...prev, isEditing: true }))} className="bg-blue-500 text-white">Edit Profile</button>
        </div>
      )}
    </div>
  )

  const VolunteerComponent = () => (
    <div className="bg-white/10 p-6 rounded-xl">
      <h4 className="font-bold">Total Hours</h4>
      <p>{state.volunteerHours}</p>
      {/* More content can be added similar to this */}
    </div>
  )

  const MembersComponent = () => (
    <div>
      {state.members.map((member, index) => <div key={index} className="bg-white/10 p-6 text-white">{member.firstName} {member.lastName} - {member.email}</div>)}
    </div>
  )

  const renderComponent = () => {
    const { title, content } = components[state.activeComponent]
    return (
      <div>
        <h3>{title}</h3>
        {content}
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gradient-to-b">
      {state.activeComponent !== 'dashboard' && <button onClick={() => setState(prev => ({ ...prev, activeComponent: 'dashboard' }))}>Back</button>}
      <h2>Welcome back, {state.firstName}!</h2>
      {renderComponent()}
    </div>
  )
}

export default Dashboard
