import React, { useState, useEffect } from 'react'
import { auth, db } from '../firebase/firebase-config'
import { signOut } from 'firebase/auth'
import { useNavigate } from 'react-router-dom'
import { doc, getDoc, collection, getDocs, query, where } from 'firebase/firestore'

const Home = () => {
  const navigate = useNavigate()
  const [firstName, setFirstName] = useState('')
  const [activeComponent, setActiveComponent] = useState('dashboard')
  const [volunteerHours, setVolunteerHours] = useState([])

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        if (auth.currentUser) {
          const userRef = doc(db, 'users', auth.currentUser.uid)
          const userSnap = await getDoc(userRef)
          if (userSnap.exists()) {
            setFirstName(userSnap.data().firstName)
          }
        }
      } catch (error) {
        console.error('Error fetching user data:', error)
      }
    }
    fetchUserData()
  }, [])

  useEffect(() => {
    const fetchVolunteerHours = async () => {
      try {
        if (auth.currentUser && activeComponent === 'volunteer') {
          const hoursQuery = query(
            collection(db, 'volunteerHours'),
            where('userId', '==', auth.currentUser.uid)
          )
          const querySnapshot = await getDocs(hoursQuery)
          const hours = []
          querySnapshot.forEach((doc) => {
            hours.push({ id: doc.id, ...doc.data() })
          })
          setVolunteerHours(hours)
        }
      } catch (error) {
        console.error('Error fetching volunteer hours:', error)
        setVolunteerHours([])
      }
    }
    fetchVolunteerHours()
  }, [activeComponent])

  const handleLogout = async () => {
    try {
      await signOut(auth)
      navigate('/login')
    } catch (error) {
      console.error('Error logging out:', error)
    }
  }

  const renderComponent = () => {
    switch (activeComponent) {
      case 'profile':
        return (
          <div className="p-6 bg-white/5 border border-white/10 rounded-lg">
            <h3 className="text-xl font-semibold text-white mb-4">Your Profile</h3>
            <p className="text-gray-300">Manage your personal information and account settings here.</p>
          </div>
        )
      case 'volunteer':
        return (
          <div className="p-6 bg-white/5 border border-white/10 rounded-lg">
            <h3 className="text-xl font-semibold text-white mb-4">Volunteer Hours</h3>
            {volunteerHours.length > 0 ? (
              <div className="space-y-4">
                <div className="grid grid-cols-4 gap-4 text-gray-300 font-semibold border-b border-white/10 pb-2">
                  <div>Event</div>
                  <div>Date</div>
                  <div>Hours</div>
                  <div>Organization</div>
                </div>
                {volunteerHours.map((hour) => (
                  <div key={hour.id} className="grid grid-cols-4 gap-4 text-gray-300">
                    <div>{hour.eventName}</div>
                    <div>{hour.date && hour.date.toDate ? new Date(hour.date.toDate()).toLocaleDateString() : 'N/A'}</div>
                    <div>{hour.hours}</div>
                    <div>{hour.organization}</div>
                  </div>
                ))}
                <div className="mt-4 pt-4 border-t border-white/10">
                  <p className="text-white font-semibold">
                    Total Hours: {volunteerHours.reduce((acc, curr) => acc + Number(curr.hours) || 0, 0)}
                  </p>
                </div>
              </div>
            ) : (
              <p className="text-gray-300">No volunteer hours recorded yet.</p>
            )}
          </div>
        )
      case 'events':
        return (
          <div className="p-6 bg-white/5 border border-white/10 rounded-lg">
            <h3 className="text-xl font-semibold text-white mb-4">Upcoming Events</h3>
            <p className="text-gray-300">View and register for upcoming volunteer opportunities.</p>
          </div>
        )
      case 'resources':
        return (
          <div className="p-6 bg-white/5 border border-white/10 rounded-lg">
            <h3 className="text-xl font-semibold text-white mb-4">Resources</h3>
            <p className="text-gray-300">Access helpful resources and documentation.</p>
          </div>
        )
      default:
        return (
          <div className="grid grid-cols-2 gap-6 mt-8">
            <div onClick={() => setActiveComponent('profile')} className="p-6 bg-white/5 border border-white/10 rounded-lg cursor-pointer hover:bg-white/10 transform transition-all duration-300 hover:scale-105">
              <h3 className="text-xl font-semibold text-white mb-4">Your Profile</h3>
              <p className="text-gray-300">Manage your personal information and account settings here.</p>
            </div>
            <div onClick={() => setActiveComponent('volunteer')} className="p-6 bg-white/5 border border-white/10 rounded-lg cursor-pointer hover:bg-white/10 transform transition-all duration-300 hover:scale-105">
              <h3 className="text-xl font-semibold text-white mb-4">Volunteer Hours</h3>
              <p className="text-gray-300">Track and manage your volunteer hours and activities.</p>
            </div>
            <div onClick={() => setActiveComponent('events')} className="p-6 bg-white/5 border border-white/10 rounded-lg cursor-pointer hover:bg-white/10 transform transition-all duration-300 hover:scale-105">
              <h3 className="text-xl font-semibold text-white mb-4">Upcoming Events</h3>
              <p className="text-gray-300">View and register for upcoming volunteer opportunities.</p>
            </div>
            <div onClick={() => setActiveComponent('resources')} className="p-6 bg-white/5 border border-white/10 rounded-lg cursor-pointer hover:bg-white/10 transform transition-all duration-300 hover:scale-105">
              <h3 className="text-xl font-semibold text-white mb-4">Resources</h3>
              <p className="text-gray-300">Access helpful resources and documentation.</p>
            </div>
          </div>
        )
    }
  }

  const buttonBaseClasses = "w-full py-3 px-6 text-white rounded-lg font-medium focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-[#132657] transition-all duration-300 shadow-lg transform hover:scale-[1.02]"
  const backButtonClasses = `${buttonBaseClasses} bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 focus:ring-blue-500 shadow-blue-500/20`
  const logoutButtonClasses = `${buttonBaseClasses} bg-gradient-to-r from-red-500 to-red-600 hover:from-red-600 hover:to-red-700 focus:ring-red-500 shadow-red-500/20`

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#132657]">
      <div className="max-w-4xl w-full space-y-8 p-10 bg-white/5 backdrop-blur-lg rounded-xl shadow-2xl border border-white/10">
        <div className="space-y-4">
          <h2 className="text-center text-4xl font-bold text-white tracking-tight">Dashboard</h2>
          <p className="text-center text-gray-300 text-xl">Hello {firstName}! Welcome back.</p>
          {activeComponent !== 'dashboard' && (
            <button
              onClick={() => setActiveComponent('dashboard')}
              className={backButtonClasses}
            >
              ‚Üê Back to Dashboard
            </button>
          )}
        </div>
        {renderComponent()}
        <button
          onClick={handleLogout}
          className={logoutButtonClasses}
        >
          Log Out
        </button>
      </div>
    </div>
  )
}

export default Home
