import React, { useState, useEffect } from 'react'
import { auth, db } from '../firebase/firebase-config'
import { updatePassword, reauthenticateWithCredential, EmailAuthProvider } from 'firebase/auth'
import { useNavigate } from 'react-router-dom'
import { doc, getDoc, updateDoc, collection, getDocs } from 'firebase/firestore'

const Dashboard = () => {
  const navigate = useNavigate()
  const [{ firstName, lastName, email, activeComponent, volunteerHours, isEditing, password, originalData, volunteerLog, bio, currentPassword, members, events }, setState] = useState({
    firstName: '', lastName: '', email: '', activeComponent: 'dashboard', volunteerHours: 0, isEditing: false, password: '', originalData: null, volunteerLog: [], bio: '', currentPassword: '', members: [], events: []
  })

  const buttonBaseClasses = "w-full py-3 px-6 text-white rounded-lg font-medium focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-[#132657] transition-all duration-300 shadow-lg transform hover:scale-[1.02]"
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        if (auth.currentUser) {
          const userSnap = await getDoc(doc(db, 'users', auth.currentUser.uid))
          if (userSnap.exists()) {
            const data = userSnap.data()
            setState(prev => ({ ...prev, firstName: data.firstName, lastName: data.lastName, email: auth.currentUser.email, volunteerHours: data.volunteerHours, bio: data.bio,
              originalData: { firstName: data.firstName, lastName: data.lastName, email: auth.currentUser.email, bio: data.bio }}))
          } else {
            console.error('User document does not exist')
          }
        } else {
          console.error('No authenticated user found')
        }
      } catch (error) {
        console.error('Error fetching user data:', error)
        alert('Failed to load user data: ' + error.message)
      }
    }
    fetchUserData()
  }, [])

  const handleUpdateProfile = async () => {
    try {
      await updateDoc(doc(db, 'users', auth.currentUser.uid), { firstName, lastName, bio, isPublic: true })
      if (password?.trim()) {
        if (!currentPassword) throw new Error('Please enter your current password to update to a new password')
        await reauthenticateWithCredential(auth.currentUser, EmailAuthProvider.credential(auth.currentUser.email, currentPassword))
        await updatePassword(auth.currentUser, password)
      }
      setState(prev => ({ ...prev, firstName, lastName, email, bio, password: '', currentPassword: '', isEditing: false, originalData: { firstName, lastName, email, bio }}))
    } catch (error) {
      console.error('Error updating profile:', error)
      alert('Failed to update profile: ' + error.message)
    }
  }

  const handleCancel = () => setState(prev => ({ ...prev, ...prev.originalData, password: '', currentPassword: '', isEditing: false }))

  const components = {
    profile: { 
      title: 'Your Profile', 
      desc: 'Manage your personal information and account settings here.',
      content: (
        <div className="space-y-4">
          <div className="bg-white/10 p-6 rounded-xl">
            {isEditing ? (
              <div className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <input type="text" placeholder="First Name" value={firstName} onChange={e => setState(prev => ({ ...prev, firstName: e.target.value }))} className="w-full p-3 rounded-lg bg-white/10 border border-white/20 text-white" maxLength={30}/>
                  <input type="text" placeholder="Last Name" value={lastName} onChange={e => setState(prev => ({ ...prev, lastName: e.target.value }))} className="w-full p-3 rounded-lg bg-white/10 border border-white/20 text-white" maxLength={30}/>
                </div>
                <input type="email" placeholder="Email" value={email} disabled className="w-full p-3 rounded-lg bg-white/10 border border-white/20 text-white opacity-50" maxLength={50}/>
                <textarea placeholder="Bio" value={bio} onChange={e => setState(prev => ({ ...prev, bio: e.target.value }))} className="w-full p-3 rounded-lg bg-white/10 border border-white/20 text-white" rows="4" maxLength={200}/>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <input type="password" placeholder="Current Password" value={currentPassword} onChange={e => setState(prev => ({ ...prev, currentPassword: e.target.value }))} className="w-full p-3 rounded-lg bg-white/10 border border-white/20 text-white" maxLength={30}/>
                  <input type="password" placeholder="New Password" value={password} onChange={e => setState(prev => ({ ...prev, password: e.target.value }))} className="w-full p-3 rounded-lg bg-white/10 border border-white/20 text-white" maxLength={30}/>
                </div>
                <div className="flex flex-col md:flex-row gap-4">
                  <button onClick={handleUpdateProfile} className={`${buttonBaseClasses} bg-gradient-to-r from-green-500 to-green-600`}>Save Changes</button>
                  <button onClick={handleCancel} className={`${buttonBaseClasses} bg-gradient-to-r from-gray-500 to-gray-600`}>Cancel</button>
                </div>
              </div>
            ) : (
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <div>
                    <h3 className="text-2xl font-bold text-white">{firstName} {lastName}</h3>
                    <p className="text-gray-300">{email}</p>
                  </div>
                  <button onClick={() => setState(prev => ({ ...prev, isEditing: true }))} className="px-4 py-2 bg-blue-500 rounded-lg hover:bg-blue-600 text-white">Edit Profile</button>
                </div>
                <div className="mt-6">
                  <h4 className="text-lg font-semibold text-white mb-2">About Me</h4>
                  <p className="text-gray-300">{bio || 'No bio added yet.'}</p>
                </div>
              </div>
            )}
          </div>
        </div>
      )
    },
    volunteer: { 
      title: 'Volunteer Activity', 
      desc: 'Track your volunteer contributions and history.',
      content: (
        <div className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="bg-white/10 p-6 rounded-xl">
              <h4 className="text-xl font-bold text-white mb-2">Total Hours</h4>
              <p className="text-3xl font-bold text-green-400">{volunteerHours}</p>
            </div>
            <div className="bg-white/10 p-6 rounded-xl">
              <h4 className="text-xl font-bold text-white mb-2">Activities</h4>
              <p className="text-3xl font-bold text-blue-400">{volunteerLog?.length || 0}</p>
            </div>
            <div className="bg-white/10 p-6 rounded-xl">
              <h4 className="text-xl font-bold text-white mb-2">Status</h4>
              <p className="text-xl font-semibold text-yellow-400">Active</p>
            </div>
          </div>
          <div className="bg-white/10 p-6 rounded-xl">
            <h4 className="text-xl font-bold text-white mb-4">Recent Activity</h4>
            <div className="space-y-4">
              {volunteerLog?.map((log, index) => (
                <div key={index} className="bg-white/5 p-4 rounded-lg border border-white/10">
                  <div className="flex justify-between items-start">
                    <div>
                      <p className="text-white font-medium">{log.description}</p>
                      <p className="text-sm text-gray-400">{log.date}</p>
                    </div>
                    <span className="px-3 py-1 bg-green-500/20 text-green-400 rounded-full">{log.hours} hours</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )
    },
    events: {
      title: 'Events',
      desc: 'View all upcoming events for volunteer opportunities.',
      content: (
        <div className="space-y-4">
          {events.map(event => (
            <div key={event.id} className="bg-white/10 p-6 rounded-xl">
              <h3 className="text-xl font-bold text-white">{event.title}</h3>
              <p className="text-gray-300">{new Date(event.date).toLocaleString()}</p>
              <p className="text-gray-300">Duration: {event.duration} hours</p>
              <p className="text-gray-300">Attendees: {event.attendees?.length || 0}</p>
              <p className="text-gray-300 mt-2">{event.description}</p>
            </div>
          ))}
        </div>
      ),
      onMount: async () => {
        const eventsSnap = await getDocs(collection(db, 'events'))
        setState(prev => ({ ...prev, events: eventsSnap.docs.map(doc => ({ id: doc.id, ...doc.data() })) }))
      }
    },
    members: { 
      title: 'Members', 
      desc: 'View all registered members.',
      content: (
        <div className="space-y-4">
          {members.map((member, index) => (
            <div key={index} className="bg-white/10 p-6 rounded-xl">
              <div className="flex flex-col space-y-2">
                <h4 className="text-xl font-bold text-white">{member.firstName} {member.lastName}</h4>
                <p className="text-gray-300">{member.email}</p>
                <p className="text-gray-300">{member.bio || 'No bio available.'}</p>
              </div>
            </div>
          ))}
        </div>
      ),
      onMount: async () => {
        const querySnapshot = await getDocs(collection(db, 'users'))
        const membersData = []
        querySnapshot.forEach((doc) => {
          membersData.push(doc.data())
        })
        setState(prev => ({ ...prev, members: membersData }))
      }
    }
  }

  useEffect(() => {
    if (activeComponent === 'members' && components[activeComponent].onMount) {
      components[activeComponent].onMount()
    }
  }, [activeComponent])

  const ComponentCard = ({ title, desc, onClick }) => (
    <div onClick={onClick} className="p-6 bg-white/10 border border-white/10 rounded-xl cursor-pointer hover:bg-white/20 transition-all duration-300">
      <h3 className="text-2xl font-bold text-white mb-3">{title}</h3>
      <p className="text-gray-300">{desc}</p>
    </div>
  )

  const renderComponent = () => {
    if (activeComponent === 'dashboard') {
      return (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {Object.entries(components).map(([key, { title, desc }]) => (
            <ComponentCard key={key} title={title} desc={desc} onClick={() => setState(prev => ({ ...prev, activeComponent: key }))} />
          ))}
        </div>
      )
    }
    const { title, content, desc } = components[activeComponent]
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-3xl font-bold text-white">{title}</h3>
            <p className="text-gray-300 mt-2">{desc}</p>
          </div>
        </div>
        {content}
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#0e1e4b] to-[#0a1735] px-4 py-8">
      <div className="max-w-7xl mx-auto">
        {activeComponent !== 'dashboard' && (
          <button onClick={() => setState(prev => ({ ...prev, activeComponent: 'dashboard' }))} className="mb-6 p-3 text-white rounded-xl font-medium bg-white/10 hover:bg-white/20 transition-all duration-300">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" /></svg>
          </button>        
        )}
        {activeComponent === 'dashboard' && (
          <div className="space-y-4 mb-8">
            <h2 className="text-4xl md:text-5xl font-bold text-white text-center">Welcome back, {firstName}!</h2>
            <p className="text-center text-gray-300 text-xl">Your volunteer dashboard awaits.</p>
          </div>
        )}
        {renderComponent()}
      </div>
    </div>
  )
}
export default Dashboard
