import React, { useState, useEffect } from 'react'
import { auth, db } from '../firebase/firebase-config'
import { signOut } from 'firebase/auth'
import { useNavigate } from 'react-router-dom'
import { doc, getDoc } from 'firebase/firestore'
import Profile from '../components/Profile'
import VolunteerHours from '../components/VolunteerHours'
import Events from '../components/Events'
import Resources from '../components/Resources'

const Home = () => {
  const navigate = useNavigate()
  const [firstName, setFirstName] = useState('')
  const [activeComponent, setActiveComponent] = useState('dashboard')

  useEffect(() => {
    const fetchUserData = async () => {
      if (auth.currentUser) {
        const userRef = doc(db, 'users', auth.currentUser.uid)
        const userSnap = await getDoc(userRef)
        if (userSnap.exists()) {
          setFirstName(userSnap.data().firstName)
        }
      }
    }
    fetchUserData()
  }, [])

  const handleLogout = async () => {
    try {
      await signOut(auth)
      navigate('/login')
    } catch (error) {
      console.error('Error logging out:', error)
    }
  }

  const renderComponent = () => {
    switch (activeComponent) {
      case 'profile':
        return <Profile />
      case 'volunteer':
        return <VolunteerHours />
      case 'events':
        return <Events />
      case 'resources':
        return <Resources />
      default:
        return <Dashboard setActiveComponent={setActiveComponent} />
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#132657]">
      <div className="max-w-4xl w-full space-y-8 p-10 bg-white/5 backdrop-blur-lg rounded-xl shadow-2xl border border-white/10">
        <div className="space-y-4">
          <h2 className="text-center text-4xl font-bold text-white tracking-tight">Dashboard</h2>
          <p className="text-center text-gray-300 text-xl">Hello {firstName}! Welcome back.</p>
          {activeComponent !== 'dashboard' && (
            <button
              onClick={() => setActiveComponent('dashboard')}
              className="w-full py-2 px-4 bg-blue-600 text-white rounded-lg font-medium hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-[#132657] transition-all duration-200 shadow-lg shadow-blue-500/20"
            >
              Back to Dashboard
            </button>
          )}
        </div>
        {renderComponent()}
        <button
          onClick={handleLogout}
          className="w-full py-3 px-4 bg-blue-600 text-white rounded-lg font-medium hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-[#132657] transition-all duration-200 shadow-lg shadow-blue-500/20"
        >
          Log Out
        </button>
      </div>
    </div>
  )}

export default Home
