import React, { useState, useEffect } from 'react'
import { db } from '../firebase/firebase-config'
import { collection, getDocs, doc, deleteDoc, addDoc, updateDoc } from 'firebase/firestore'

const AdminDashboard = () => {
  const [users, setUsers] = useState([])
  const [events, setEvents] = useState([])
  const [newEvent, setNewEvent] = useState({ title: '', date: '', duration: '', description: '', attendees: [] })
  const [activeSession, setActiveSession] = useState(null)
  const [activeComponent, setActiveComponent] = useState('dashboard')

  useEffect(() => {
    fetchUsers()
    fetchEvents()
  }, [])

  const fetchUsers = async () => {
    const querySnapshot = await getDocs(collection(db, 'users'))
    const usersData = []
    querySnapshot.forEach((doc) => {
      usersData.push({ id: doc.id, ...doc.data() })
    })
    setUsers(usersData)
  }

  const fetchEvents = async () => {
    const querySnapshot = await getDocs(collection(db, 'events'))
    const eventsData = []
    querySnapshot.forEach((doc) => {
      eventsData.push({ id: doc.id, ...doc.data() })
    })
    setEvents(eventsData)
  }

  const handleRemoveUser = async (userId) => {
    try {
      await deleteDoc(doc(db, 'users', userId))
      setUsers(users.filter(user => user.id !== userId))
    } catch (error) {
      console.error('Error removing user:', error)
    }
  }

  const handleCreateEvent = async () => {
    try {
      await addDoc(collection(db, 'events'), {
        ...newEvent,
        createdAt: new Date().toISOString(),
        status: 'upcoming'
      })
      setNewEvent({ title: '', date: '', duration: '', description: '', attendees: [] })
      fetchEvents()
    } catch (error) {
      console.error('Error creating event:', error)
    }
  }

  const startVolunteerSession = async (eventId) => {
    try {
      const eventRef = doc(db, 'events', eventId)
      await updateDoc(eventRef, { status: 'active' })
      setActiveSession(eventId)
    } catch (error) {
      console.error('Error starting session:', error)
    }
  }

  const endVolunteerSession = async (eventId) => {
    try {
      const eventRef = doc(db, 'events', eventId)
      const event = events.find(e => e.id === eventId)

      // Update attendees' volunteer hours
      for (const attendeeId of event.attendees) {
        const userRef = doc(db, 'users', attendeeId)
        const userDoc = users.find(u => u.id === attendeeId)

        await updateDoc(userRef, {
          volunteerHours: (userDoc.volunteerHours || 0) + parseInt(event.duration),
          volunteerLog: [...(userDoc.volunteerLog || []), {
            description: event.title,
            date: new Date().toISOString(),
            hours: parseInt(event.duration)
          }]
        })
      }

      await updateDoc(eventRef, { status: 'completed' })
      setActiveSession(null)
      fetchEvents()
      fetchUsers()
    } catch (error) {
      console.error('Error ending session:', error)
    }
  }

  return (
    <div className="min-h-screen bg-[#0e1e4b] px-4 py-8">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-4xl font-bold text-white mb-8">Admin Dashboard</h1>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-8">
          <div className="bg-white/5 backdrop-blur-lg rounded-xl p-6 border border-white/10">
            <h2 className="text-2xl font-bold text-white mb-4">Manage Users</h2>
            <div className="space-y-4">
              {users.map(user => (
                <div key={user.id} className="flex items-center justify-between bg-white/10 p-4 rounded-lg">
                  <div>
                    <p className="text-white font-medium">{user.firstName} {user.lastName}</p>
                    <p className="text-gray-300 text-sm">{user.email}</p>
                  </div>
                  {!user.email.includes('genlinkaustin@gmail.com') && (
                    <button
                      onClick={() => handleRemoveUser(user.id)}
                      className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600"
                    >
                      Remove
                    </button>
                  )}
                </div>
              ))}
            </div>
          </div>

          <div className="bg-white/5 backdrop-blur-lg rounded-xl p-6 border border-white/10">
            <h2 className="text-2xl font-bold text-white mb-4">Create Event</h2>
            <div className="space-y-4">
              <input
                type="text"
                placeholder="Event Title"
                value={newEvent.title}
                onChange={e => setNewEvent({...newEvent, title: e.target.value})}
                className="w-full p-3 rounded-lg bg-white/10 border border-white/20 text-white"
              />
              <input
                type="datetime-local"
                value={newEvent.date}
                onChange={e => setNewEvent({...newEvent, date: e.target.value})}
                className="w-full p-3 rounded-lg bg-white/10 border border-white/20 text-white"
              />
              <input
                type="number"
                placeholder="Duration (hours)"
                value={newEvent.duration}
                onChange={e => setNewEvent({...newEvent, duration: e.target.value})}
                className="w-full p-3 rounded-lg bg-white/10 border border-white/20 text-white"
              />
              <textarea
                placeholder="Description"
                value={newEvent.description}
                onChange={e => setNewEvent({...newEvent, description: e.target.value})}
                className="w-full p-3 rounded-lg bg-white/10 border border-white/20 text-white"
                rows="4"
              />
              <button
                onClick={handleCreateEvent}
                className="w-full py-3 px-4 bg-blue-600 text-white rounded-lg font-medium hover:bg-blue-700"
              >
                Create Event
              </button>
            </div>
          </div>
        </div>

        <div className="bg-white/5 backdrop-blur-lg rounded-xl p-6 border border-white/10">
          <h2 className="text-2xl font-bold text-white mb-4">Manage Events</h2>
          <div className="space-y-4">
            {events.map(event => (
              <div key={event.id} className="bg-white/10 p-4 rounded-lg">
                <div className="flex justify-between items-start">
                  <div>
                    <h3 className="text-white font-medium">{event.title}</h3>
                    <p className="text-gray-300 text-sm">{new Date(event.date).toLocaleString()}</p>
                    <p className="text-gray-300 text-sm">Duration: {event.duration} hours</p>
                    <p className="text-gray-300 text-sm">Attendees: {event.attendees?.length || 0}</p>
                  </div>
                  {event.status === 'upcoming' && (
                    <button
                      onClick={() => startVolunteerSession(event.id)}
                      className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
                    >
                      Start Session
                    </button>
                  )}
                  {event.status === 'active' && (
                    <button
                      onClick={() => endVolunteerSession(event.id)}
                      className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600"
                    >
                      End Session
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  )
}

export default AdminDashboard
