import React, { useState, useEffect } from 'react'
import { auth, db } from '../firebase/firebase-config'
import { collection, addDoc, query, where, getDocs } from 'firebase/firestore'

const VolunteerHours = () => {
  const [hours, setHours] = useState('')
  const [description, setDescription] = useState('')
  const [date, setDate] = useState('')
  const [volunteerHistory, setVolunteerHistory] = useState([])

  useEffect(() => {
    fetchVolunteerHours()
  }, [])

  const fetchVolunteerHours = async () => {
    try {
      const q = query(
        collection(db, 'volunteerHours'),
        where('userId', '==', auth.currentUser.uid)
      )
      const querySnapshot = await getDocs(q)
      const hours = []
      querySnapshot.forEach((doc) => {
        hours.push({ id: doc.id, ...doc.data() })
      })
      setVolunteerHistory(hours)
    } catch (error) {
      console.error('Error fetching volunteer hours:', error)
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      await addDoc(collection(db, 'volunteerHours'), {
        userId: auth.currentUser.uid,
        hours: Number(hours),
        description,
        date,
        timestamp: new Date()
      })
      setHours('')
      setDescription('')
      setDate('')
      fetchVolunteerHours()
    } catch (error) {
      console.error('Error adding volunteer hours:', error)
    }
  }

  return (
    <div className="space-y-6">
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-gray-300 mb-2">Hours</label>
          <input
            type="number"
            value={hours}
            onChange={(e) => setHours(e.target.value)}
            className="w-full px-4 py-2 rounded-lg bg-white/10 border border-white/20 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <div>
          <label className="block text-gray-300 mb-2">Description</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full px-4 py-2 rounded-lg bg-white/10 border border-white/20 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <div>
          <label className="block text-gray-300 mb-2">Date</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full px-4 py-2 rounded-lg bg-white/10 border border-white/20 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <button
          type="submit"
          className="w-full py-2 px-4 bg-blue-600 text-white rounded-lg font-medium hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-[#132657] transition-all duration-200 shadow-lg shadow-blue-500/20"
        >
          Log Hours
        </button>
      </form>

      <div className="space-y-4">
        <h3 className="text-xl font-semibold text-white">Volunteer History</h3>
        {volunteerHistory.map((entry) => (
          <div
            key={entry.id}
            className="p-4 rounded-lg bg-white/10 border border-white/20"
          >
            <p className="text-white">Hours: {entry.hours}</p>
            <p className="text-gray-300">{entry.description}</p>
            <p className="text-gray-400">{entry.date}</p>
          </div>
        ))}
      </div>
    </div>
  )
}

export default VolunteerHours
