import React, { useState, useEffect } from 'react'
import { auth, db } from '../firebase/firebase-config'
import { signOut, updatePassword, reauthenticateWithCredential, EmailAuthProvider } from 'firebase/auth'
import { useNavigate } from 'react-router-dom'
import { doc, getDoc, updateDoc, collection, getDocs } from 'firebase/firestore'

const Home = () => {
  const navigate = useNavigate()
  const [{ firstName, lastName, email, activeComponent, volunteerHours, isEditing, password, originalData, members, volunteerLog, bio, currentPassword }, setState] = useState({
    firstName: '', lastName: '', email: '', activeComponent: 'dashboard', volunteerHours: 0, isEditing: false, password: '', originalData: null, members: [], volunteerLog: [], bio: '', currentPassword: ''
  })

  const buttonBaseClasses = "w-full py-3 px-6 text-white rounded-lg font-medium focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-[#132657] transition-all duration-300 shadow-lg transform hover:scale-[1.02]"

  useEffect(() => {
    const fetchUserData = async () => {
      if (auth.currentUser) {
        const userSnap = await getDoc(doc(db, 'users', auth.currentUser.uid))
        if (userSnap.exists()) {
          const data = userSnap.data()
          setState(prev => ({ ...prev, firstName: data.firstName, lastName: data.lastName, email: auth.currentUser.email, volunteerHours: data.volunteerHours, bio: data.bio,
            originalData: { firstName: data.firstName, lastName: data.lastName, email: auth.currentUser.email, bio: data.bio }}))
        }
      }
    }
    fetchUserData()
  }, [])

  const handleLogout = async () => {
    try { await signOut(auth); navigate('/login') } catch (error) { console.error('Error logging out:', error) }
  }

  const handleUpdateProfile = async () => {
    try {
      const userRef = doc(db, 'users', auth.currentUser.uid)
      await updateDoc(userRef, { firstName, lastName, bio })

      if (password && password.trim() !== '') {
        if (!currentPassword) {
          throw new Error('Please enter your current password to update to a new password')
        }
        const credential = EmailAuthProvider.credential(auth.currentUser.email, currentPassword)
        await reauthenticateWithCredential(auth.currentUser, credential)
        await updatePassword(auth.currentUser, password)
      }
      setState(prev => ({ ...prev, firstName, lastName, email, bio, password: '', currentPassword: '', isEditing: false, originalData: { firstName, lastName, email, bio }}))
    } catch (error) {
      console.error('Error updating profile:', error)
      alert('Failed to update profile: ' + error.message)
    }
  }

  const handleCancel = () => setState(prev => ({ ...prev, firstName: prev.originalData.firstName, lastName: prev.originalData.lastName, email: prev.originalData.email, bio: prev.originalData.bio, password: '', currentPassword: '', isEditing: false }))

  const fetchMembers = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, 'users'))
      const membersList = []
      querySnapshot.forEach((doc) => membersList.push({ id: doc.id, firstName: doc.data().firstName || '', lastName: doc.data().lastName || '', email: doc.data().email || '', volunteerHours: doc.data().volunteerHours || 0 }))
      setState(prev => ({ ...prev, members: membersList }))
    } catch (error) { console.error('Error fetching members:', error) }
  }

  useEffect(() => { if (activeComponent === 'members') fetchMembers() }, [activeComponent])

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#132657]">
      <div className="max-w-4xl w-full space-y-8 p-10 bg-white/5 backdrop-blur-lg rounded-xl shadow-2xl border border-white/10">
        {activeComponent !== 'dashboard' && (
          <button onClick={() => setState(prev => ({ ...prev, activeComponent: 'dashboard' }))} className="p-2 text-white rounded-lg font-medium focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-[#132657] transition-all duration-300 shadow-lg transform hover:scale-[1.02] bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 focus:ring-blue-500 shadow-blue-500/20 absolute top-4 left-4">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" /></svg>
          </button>        
        )}
        {activeComponent === 'dashboard' && (
          <div className="space-y-4 mb-8">
            <h2 className="text-center text-4xl font-bold text-white tracking-tight">Dashboard</h2>
            <p className="text-center text-gray-300 text-xl">Hello {firstName}! Welcome back.</p>
          </div>
        )}
        <DashboardContent 
          activeComponent={activeComponent}
          components={components}
          setState={setState}
          firstName={firstName}
          lastName={lastName}
          email={email}
          bio={bio}
          isEditing={isEditing}
          currentPassword={currentPassword}
          password={password}
          volunteerHours={volunteerHours}
          volunteerLog={volunteerLog}
          members={members}
          handleUpdateProfile={handleUpdateProfile}
          handleCancel={handleCancel}
          buttonBaseClasses={buttonBaseClasses}
          auth={auth}
        />
        <button onClick={handleLogout} className={`${buttonBaseClasses} bg-gradient-to-r from-red-500 to-red-600 hover:from-red-600 hover:to-red-700 focus:ring-red-500 shadow-red-500/20`}>Log Out</button>
      </div>
    </div>
  )
}

export default Home

// Separate Components
const ComponentCard = ({ title, desc, onClick }) => (
  <div onClick={onClick} className="p-6 bg-white/5 border border-white/10 rounded-lg cursor-pointer hover:bg-white/10 transform transition-all duration-300 hover:scale-105">
    <h3 className="text-xl font-semibold text-white mb-4">{title}</h3>
    <p className="text-gray-300">{desc}</p>
  </div>
)

const DashboardContent = ({ activeComponent, components, setState, firstName, lastName, email, bio, isEditing, currentPassword, password, volunteerHours, volunteerLog, members, handleUpdateProfile, handleCancel, buttonBaseClasses, auth }) => {
  if (activeComponent === 'dashboard') {
    return (
      <div className="grid grid-cols-2 gap-6">
        {Object.entries(components).map(([key, { title, desc }]) => (
          <ComponentCard key={key} title={title} desc={desc} onClick={() => setState(prev => ({ ...prev, activeComponent: key }))} />
        ))}
      </div>
    )
  }
  const { title, content, desc } = components[activeComponent]
  return (
    <div className="p-6 bg-white/5 border border-white/10 rounded-lg">
      <h3 className="text-xl font-semibold text-white mb-4">{title}</h3>
      {content || <p className="text-gray-300">{desc}</p>}
    </div>
  )
}

