import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { motion, useAnimation, useScroll, useTransform } from 'framer-motion'
import { AcademicCapIcon, HandRaisedIcon, HeartIcon, ComputerDesktopIcon, UserGroupIcon, UserIcon } from '@heroicons/react/24/outline'

const Home = () => {
  const controls = useAnimation()
  const { scrollYProgress } = useScroll()
  const scale = useTransform(scrollYProgress, [0, 1], [1, 1.2])
  const opacity = useTransform(scrollYProgress, [0, 0.3], [1, 0])
  const backgroundY = useTransform(scrollYProgress, [0, 1], ['0%', '50%'])

  const fadeIn = {
    initial: { opacity: 0, y: 20 },
    animate: { opacity: 1, y: 0 },
    transition: { duration: 0.6 }
  }

  const cardHover = {
    scale: 1.05,
    rotate: 2,
    transition: { type: "spring", stiffness: 300 }
  }

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        delayChildren: 0.3,
        staggerChildren: 0.2
      }
    }
  }

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        type: "spring",
        stiffness: 100
      }
    }
  }

  useEffect(() => {
    const sequence = async () => {
      await controls.start({ scale: [1, 1.2, 1], rotate: [0, 5, -5, 0] })
      await controls.start({ y: [0, -20, 0], transition: { repeat: Infinity, duration: 2 } })
    }
    sequence()
  }, [controls])

  return (
    <motion.div 
      className="min-h-screen bg-[#0e1e4b] relative overflow-hidden"
      style={{ backgroundPositionY: backgroundY }}
    >
      <motion.div 
        className="absolute inset-0 bg-gradient-to-b from-blue-500/20 to-purple-500/20"
        style={{ opacity: scrollYProgress }}
      />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div 
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="pt-20 pb-12"
          style={{ scale, opacity }}
        >
          <div className="flex items-center justify-center space-x-12">
            <motion.div
              animate={controls}
              initial={{ scale: 0.5 }}
            >
              <img src={require("../assets/genlink-logo.png")} alt="GenLink Logo" className="h-40" />
            </motion.div>
            <div className="text-left">
              <motion.h1 
                className="text-6xl font-bold text-white mb-6"
                animate={{ 
                  textShadow: ["0 0 5px #fff", "0 0 20px #fff", "0 0 5px #fff"],
                }}
                transition={{ duration: 2, repeat: Infinity }}
              >
                Welcome to GenLink
              </motion.h1>
              <motion.p 
                className="text-2xl text-gray-300 max-w-3xl"
                {...fadeIn}
              >
                Bridging generations through technology, creating meaningful connections that last a lifetime.
              </motion.p>
            </div>
          </div>
        </motion.div>

        <motion.div 
          className="grid grid-cols-1 md:grid-cols-3 gap-8 py-12"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: false, amount: 0.3 }}
        >
          {[
            {
              icon: <ComputerDesktopIcon className="w-10 h-10 text-blue-400 mb-4" />,
              title: "Interactive Learning",
              content: "Engage in creative projects that bridge generations through technology and shared experiences.",
              items: ["Virtual workshops and tutorials", "Interactive coding sessions", "Digital storytelling projects"]
            },
            {
              icon: <UserGroupIcon className="w-10 h-10 text-purple-400 mb-4" />,
              title: "Community Building",
              content: "Foster meaningful connections in our vibrant community through:",
              items: ["Weekly virtual meetups", "Mentorship programs", "Group activities and events"]
            },
            {
              icon: <AcademicCapIcon className="w-10 h-10 text-green-400 mb-4" />,
              title: "Personal Growth",
              content: "Develop new skills and expand your horizons through:",
              items: ["Skill-sharing workshops", "Personal development tracks", "Certificate programs"]
            }
          ].map((card, index) => (
            <motion.div 
              key={index}
              className="p-8 bg-white/5 backdrop-blur-lg rounded-xl shadow-2xl border border-white/10 relative overflow-hidden group"
              variants={itemVariants}
              whileHover={cardHover}
              whileInView={{
                x: [50, 0],
                opacity: [0, 1],
                transition: { delay: index * 0.2 }
              }}
            >
              <motion.div 
                className="absolute inset-0 bg-gradient-to-r from-blue-500/10 to-purple-500/10"
                initial={{ opacity: 0 }}
                whileHover={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
              />
              {card.icon}
              <motion.h3 
                className="text-2xl font-bold text-white mb-4"
                whileHover={{ scale: 1.05, color: "#60A5FA" }}
              >
                {card.title}
              </motion.h3>
              <p className="text-gray-300">{card.content}</p>
              <ul className="mt-4 text-gray-300 space-y-2">
                {card.items.map((item, i) => (
                  <motion.li
                    key={i}
                    initial={{ opacity: 0, x: -20 }}
                    whileInView={{ opacity: 1, x: 0 }}
                    transition={{ delay: i * 0.2 }}
                  >
                    â€¢ {item}
                  </motion.li>
                ))}
              </ul>
            </motion.div>
          ))}
        </motion.div>

        <motion.div 
          className="py-16"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: false }}
        >
          <div className="text-center space-y-8">
            <motion.h2 
              className="text-4xl font-bold text-white"
              animate={{ 
                color: ["#fff", "#60A5FA", "#fff"],
              }}
              transition={{ duration: 3, repeat: Infinity }}
            >
              Why Choose GenLink?
            </motion.h2>
            <motion.div 
              className="grid grid-cols-1 md:grid-cols-3 gap-8 mt-12"
              variants={containerVariants}
              initial="hidden"
              whileInView="visible"
            >
              {[
                { icon: <HandRaisedIcon className="w-12 h-12 text-blue-400 mb-4" />, title: "Meaningful Connections", desc: "Build lasting relationships across generations" },
                { icon: <UserIcon className="w-12 h-12 text-purple-400 mb-4" />, title: "Expert Guidance", desc: "Learn from experienced mentors" },
                { icon: <HeartIcon className="w-12 h-12 text-pink-400 mb-4" />, title: "Supportive Community", desc: "Join a welcoming and inclusive environment" }
              ].map((item, index) => (
                <motion.div 
                  key={index}
