import React, { useState, useEffect } from 'react'
import { auth, db } from '../firebase/firebase-config'
import { updatePassword, reauthenticateWithCredential, EmailAuthProvider } from 'firebase/auth'
import { useNavigate } from 'react-router-dom'
import { doc, getDoc, updateDoc, collection, getDocs } from 'firebase/firestore'

const Dashboard = () => {
  const navigate = useNavigate()
  const [{ firstName, lastName, email, activeComponent, volunteerHours, isEditing, password, originalData, volunteerLog, bio, currentPassword, members }, setState] = useState({
    firstName: '', lastName: '', email: '', activeComponent: 'dashboard', volunteerHours: 0, isEditing: false, password: '', originalData: null, volunteerLog: [], bio: '', currentPassword: '', members: []
  })

  const buttonBaseClasses = "w-full py-3 px-6 text-white rounded-lg font-medium focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-[#132657] transition-all duration-300 shadow-lg transform hover:scale-[1.02]"

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        if (auth.currentUser) {
          const userSnap = await getDoc(doc(db, 'users', auth.currentUser.uid))
          if (userSnap.exists()) {
            const data = userSnap.data()
            setState(prev => ({ ...prev, firstName: data.firstName, lastName: data.lastName, email: auth.currentUser.email, volunteerHours: data.volunteerHours, bio: data.bio,
              originalData: { firstName: data.firstName, lastName: data.lastName, email: auth.currentUser.email, bio: data.bio }}))
          }
        }
      } catch (error) {
        alert('Failed to load user data: ' + error.message)
      }
    }
    fetchUserData()
  }, [])

  const handleUpdateProfile = async () => {
    try {
      await updateDoc(doc(db, 'users', auth.currentUser.uid), { firstName, lastName, bio, isPublic: true })
      if (password?.trim()) {
        if (!currentPassword) throw new Error('Please enter your current password')
        await reauthenticateWithCredential(auth.currentUser, EmailAuthProvider.credential(auth.currentUser.email, currentPassword))
        await updatePassword(auth.currentUser, password)
      }
      setState(prev => ({ ...prev, firstName, lastName, email, bio, password: '', currentPassword: '', isEditing: false, originalData: { firstName, lastName, email, bio }}))
    } catch (error) {
      alert('Failed to update profile: ' + error.message)
    }
  }

  const handleCancel = () => setState(prev => ({ ...prev, ...prev.originalData, password: '', currentPassword: '', isEditing: false }))

  const components = {
    profile: { 
      title: 'Your Profile',
      content: (
        <div className="bg-white/10 p-6 rounded-xl">
          {isEditing ? (
            <div className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <input type="text" placeholder="First Name" value={firstName} onChange={e => setState(prev => ({ ...prev, firstName: e.target.value }))} className="w-full p-3 rounded-lg bg-white/10 border border-white/20 text-white"/>
                <input type="text" placeholder="Last Name" value={lastName} onChange={e => setState(prev => ({ ...prev, lastName: e.target.value }))} className="w-full p-3 rounded-lg bg-white/10 border border-white/20 text-white"/>
              </div>
              <textarea placeholder="Bio" value={bio} onChange={e => setState(prev => ({ ...prev, bio: e.target.value }))} className="w-full p-3 rounded-lg bg-white/10 border border-white/20 text-white" rows="4"/>
              <div className="flex gap-4">
                <button onClick={handleUpdateProfile} className={`${buttonBaseClasses} bg-green-500`}>Save</button>
                <button onClick={handleCancel} className={`${buttonBaseClasses} bg-gray-500`}>Cancel</button>
              </div>
            </div>
          ) : (
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <h3 className="text-2xl font-bold text-white">{firstName} {lastName}</h3>
                <button onClick={() => setState(prev => ({ ...prev, isEditing: true }))} className="px-4 py-2 bg-blue-500 rounded-lg text-white">Edit</button>
              </div>
              <p className="text-gray-300">{bio || 'No bio added yet.'}</p>
            </div>
          )}
        </div>
      )
    },
    volunteer: { 
      title: 'Volunteer Activity',
      content: (
        <div className="space-y-6">
          <div className="bg-white/10 p-6 rounded-xl">
            <h4 className="text-xl font-bold text-white mb-2">Total Hours: {volunteerHours}</h4>
            <div className="space-y-4">
              {volunteerLog?.map((log, index) => (
                <div key={index} className="bg-white/5 p-4 rounded-lg">
                  <p className="text-white">{log.description} - {log.hours} hours</p>
                  <p className="text-sm text-gray-400">{log.date}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      )
    },
    members: { 
      title: 'Members',
      content: (
        <div className="space-y-4">
          {members.map((member, index) => (
            <div key={index} className="bg-white/10 p-6 rounded-xl">
              <h4 className="text-xl font-bold text-white">{member.firstName} {member.lastName}</h4>
              <p className="text-gray-300">{member.bio || 'No bio available.'}</p>
            </div>
          ))}
        </div>
      ),
      onMount: async () => {
        const querySnapshot = await getDocs(collection(db, 'users'))
        setState(prev => ({ ...prev, members: querySnapshot.docs.map(doc => doc.data()) }))
      }
    }
  }

  useEffect(() => {
    if (activeComponent === 'members' && components[activeComponent].onMount) {
      components[activeComponent].onMount()
    }
  }, [activeComponent])

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#0e1e4b] to-[#0a1735] p-8">
      <div className="max-w-7xl mx-auto">
        {activeComponent !== 'dashboard' && (
          <button onClick={() => setState(prev => ({ ...prev, activeComponent: 'dashboard' }))} className="mb-6 p-3 text-white rounded-xl bg-white/10">
            Back
          </button>        
        )}
        {activeComponent === 'dashboard' ? (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {Object.entries(components).map(([key, { title }]) => (
              <div key={key} onClick={() => setState(prev => ({ ...prev, activeComponent: key }))} className="p-6 bg-white/10 rounded-xl cursor-pointer">
                <h3 className="text-2xl font-bold text-white">{title}</h3>
              </div>
            ))}
          </div>
        ) : (
          <div>{components[activeComponent].content}</div>
        )}
      </div>
    </div>
  )
}

export default Dashboard
